document.addEventListener("DOMContentLoaded",()=>{gsap.registerPlugin(ScrollTrigger),gsap.utils.toArray(".clip-img").forEach(r=>{gsap.fromTo(r,{clipPath:"inset(0 100% 0 0)"},{clipPath:"inset(0 0 0 0)",scrollTrigger:{trigger:r,start:"top 70%",end:"bottom 70%",markers:!0}})}),gsap.utils.toArray(".clip-img-reverse").forEach(r=>{gsap.fromTo(r,{clipPath:"inset(0 0 0 100%)"},{clipPath:"inset(0 0 0 0)",scrollTrigger:{trigger:r,start:"top 70%",end:"bottom 70%",markers:!0}})}),window.addEventListener("reload",()=>{ScrollTrigger.refresh()})});